from __future__ import print_function
__Author__ = 'Wei Song'
import numpy as np
np.random.seed(1337)  # for reproducibility
from keras import backend as K
from keras.models import Sequential, Model
from keras.layers import Input
from keras.layers import Dense, Dropout, Activation, Flatten, Lambda, Merge, merge, Flatten, Reshape, Permute, RepeatVector
from keras.layers import Convolution2D, MaxPooling2D
from keras.layers import LSTM, GRU, SimpleRNN
from keras.layers import TimeDistributed
from keras.layers import Embedding
from readData import max_npara, max_nsent
import time
from __init__ import timeDecor


embedding_dim = 12
nb_pool = 2
nb_epoch = 1
savedFile = ''



def cnn_model(n_class):
    '''
    使用卷积神经网络处理文章结构进行分类
    :param n_class:
    :return: 模型model
    '''
    # 所有数据的输入形状
    text_in = Input(shape=(847, max_npara, max_nsent, embedding_dim))

    # 段落模型
    essay_input = Input(shape=(20, 10, 12))
    x = Convolution2D(32, 3, 3, border_mode='valid')(essay_input)
    x = Activation('relu')(x)
    x = Convolution2D(32, 3, 3)(x)
    x = Activation('relu')(x)
    x = MaxPooling2D(pool_size=(nb_pool, nb_pool))(x)
    x = Dropout(0.25)(x)
    x = Flatten()(x)
    x = Dense(128)(x)
    x = Activation('relu')(x)
    x = Dropout(0.5)(x)
    x = Dense(n_class)(x)
    prediction = Activation('softmax')(x)

    essay_model = Model(input=essay_input, output=prediction)
    essay_model.compile(optimizer='rmsprop',
                  loss='categorical_crossentropy',
                  metrics=['accuracy'])
    print(essay_model.summary())
    global savedFile
    savedFile = r'savedModel/cnn{0}.h5'.format(
        time.strftime('_%Y%m%d_%H%M%S', time.localtime(time.time()))
    )
    return essay_model




def hierarchical_model(n_class):
    ''' 用于处理每一篇文档
    '''
    # 所有数据的输入形状
    text_in = Input(shape=(847, max_npara, max_nsent, embedding_dim))

    # 段落模型
    para_input = Input(shape=(max_nsent, embedding_dim))
    sentence_forward = GRU(embedding_dim, return_sequences=False)(para_input)
    sentence_backward = GRU(embedding_dim, return_sequences=False, go_backwards=True)(para_input)
    #sentence_rev = Lambda(lambda x: K.permute_dimensions(K.permute_dimensions(x, (1,0))[:-1], (1,0)), output_shape = lambda shape: shape)(sentence_backward)

    #gru_rev = para_backward.reverse()
    para_output = merge([sentence_forward,sentence_backward], mode="concat", concat_axis=-1)
    para_model = Model(input=para_input, output=para_output)

    # 篇章模型
    essay_input = Input(shape=(max_npara, max_nsent, embedding_dim))
    paravectors = TimeDistributed(para_model)(essay_input)
    print(paravectors)
    para_dim = embedding_dim * 2
    para_forward = GRU(para_dim, return_sequences=False)(paravectors)
    para_backward = GRU(para_dim, return_sequences=False, go_backwards=True)(paravectors)
    #para_rev = Lambda(lambda x: K.permute_dimensions(K.permute_dimensions(x, (1,0))[:-1], (1,0)), output_shape = lambda shape: shape)(para_backward)
    essay_output = merge([para_forward,para_backward], mode="concat", concat_axis=-1)
    #attention = Dense(1, activation='linear')(gru_forward)
    essay_output = Dropout(p=0.25)(essay_output)
    prediction = Dense(n_class, activation='softmax')(essay_output)

    essay_model = Model(input=essay_input, output=prediction)
    essay_model.compile(loss='categorical_crossentropy', optimizer='Adam', metrics=['accuracy'])
    #attention_value_model = Model(input=text_in, output=attention_sent_3)
    #predict = gru_attention_model.predict(text)
    #print(predict.shape)
    print(essay_model.summary())
    global savedFile
    savedFile = r'savedModel/hierarchical{0}.h5'.format(
        time.strftime('_%Y%m%d_%H%M%S', time.localtime(time.time()))
    )
    return essay_model

def hierarchical_attention_model():
    ''' 用于处理每一篇文档
    '''
    # 所有数据的输入形状
    text_in = Input(shape=(847, max_npara, max_nsent, embedding_dim))

    # 段落模型
    para_input = Input(shape=(max_nsent, embedding_dim))
    gru_forward = GRU(embedding_dim, return_sequences=False)(para_input)
    #gru_backward = GRU(embedding_dim, return_sequences=False, go_backwards=True)(para_input)
    #gru_rev = Lambda(lambda x: K.permute_dimensions(K.permute_dimensions(x, (1,0,2))[::-1], (1,0,2)), output_shape = lambda shape: shape)(gru_backward)
    #gru_all = merge([gru_forward, gru_backward], mode="concat", concat_axis=-1)
    para_model = Model(input=para_input, output=gru_forward)

    # 篇章模型
    para_dim = embedding_dim# * 2
    essay_input = Input(shape=(max_npara, max_nsent, para_dim))
    paravectors = TimeDistributed(para_model)(essay_input)
    print(paravectors)
    gru_forward = LSTM(para_dim, return_sequences=False)(paravectors)
    #attention = Dense(1, activation='linear')(gru_forward)
